async def handle_message(update: Update, context: ContextTypes.DEFAULT_TYPE):
    url = update.message.text.strip()
    user_id = update.message.from_user.id
    lang = user_lang.get(user_id, "uz")

    if any(x in url for x in ["youtube.com", "youtu.be", "tiktok.com", "instagram.com"]):
        msg = await update.message.reply_text("ðŸ“¥ Video yuklanmoqda, kuting...")
        await download_video(url, msg, context, user_id)
    else:
        texts_invalid = {
            "uz": "âŒ Bu faqat YouTube, TikTok va Instagram videolari uchun ishlaydi.",
            "ru": "âŒ Ð Ð°Ð±Ð¾Ñ‚Ð°ÐµÑ‚ Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ñ Ð²Ð¸Ð´ÐµÐ¾ YouTube, TikTok Ð¸ Instagram.",
            "en": "âŒ Only works for YouTube, TikTok, and Instagram videos.",
            "de": "âŒ Funktioniert nur mit YouTube-, TikTok- und Instagram-Videos."
        }
        await update.message.reply_text(texts_invalid[lang])

